```const ACCESS_TOKEN = 'ACCESS_TOKEN_GOES_HERE';

function onOpen() {
  const ui = SpreadsheetApp.getUi();
  const menu = ui.createMenu('HubSpot');
  menu.addItem('Test Merge', 'testMergeContacts');
  menu.addToUi();
}

function testMergeContacts() {
  const sheet = SpreadsheetApp.getActiveSpreadsheet().getSheetByName('Sheet1');
  const log = [];

  const existingContacts = getStoredContacts(sheet);
  const newContacts = getAllContacts(log);
  const allContacts = existingContacts.concat(newContacts);

  storeContacts(sheet, allContacts);

  const groupedContacts = groupContacts(allContacts);
  mergeContacts(groupedContacts, log);

  sheet.getRange('A1').setValue(log.join('\n'));
}

function callHubSpotApi(url, method = 'get', payload) {
  const options = {
    method,
    headers: {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${ACCESS_TOKEN}`,
    },
    muteHttpExceptions: true,
  };

  if (payload) {
    options.payload = JSON.stringify(payload);
  }

  const response = UrlFetchApp.fetch(url, options);
  if (response.getResponseCode() !== 200) {
    const responseCode = response.getResponseCode();
    const responseText = response.getContentText();
    const errorMessage = `HubSpot API request failed (Response Code: ${responseCode}): ${responseText}`;
    throw new Error(errorMessage);
  }

  return JSON.parse(response.getContentText());
}

function getAllContacts(log) {
  let contacts = [];
  const batchSize = 100;
  const checkMergesAt = 1000;
  let contactsSinceLastMergeCheck = 0;

  const scriptProperties = PropertiesService.getScriptProperties();
  let after = scriptProperties.getProperty('after') || '';

  while (contactsSinceLastMergeCheck &lt; checkMergesAt) {
    const apiUrl = `<https://api.hubapi.com/crm/v3/objects/contacts?limit=${batchSize}&amp;properties=email,firstname,lastname,phone,SPID,createdate,HubSpot_owner_id${after> ? `&amp;after=${after}` : ''}`;

    const response = callHubSpotApi(apiUrl);
    contacts = contacts.concat(response.results);
    contactsSinceLastMergeCheck += response.results.length;

    if (!response.paging || !response.paging.next) {
      break;
    }
    after = response.paging.next.after;
    Utilities.sleep(500); // Adjust the delay in milliseconds according to your API rate limits
  }

  scriptProperties.setProperty('after', after);

  log.push(`Retrieved contacts up to the limit.`);
  return contacts;
}

function groupContacts(contacts) {
  const groups = {};

  contacts.forEach(contact =&gt; {
    const email = contact.properties.email || '';
    const firstname = contact.properties.firstname || '';
    const lastname = contact.properties.lastname || '';
    const phone = contact.properties.phone || '';

    const key = [email, firstname + ' ' + lastname, phone].join('|');

    if (!groups[key]) {
      groups[key] = [];
    }

    groups[key].push(contact);
  });

  return Object.values(groups).filter(group =&gt; group.length &gt; 1);
}

function mergeContacts(groups, log) {
  for (const group of groups) {
    group.sort((a, b) =&gt; {
      if (a.properties.SPID &amp;&amp; !b.properties.SPID) return -1;
      if (!a.properties.SPID &amp;&amp; b.properties.SPID) return 1;
      return new Date(a.properties.createdate) - new Date(b.properties.createdate);
    });
    const primaryContact = group.shift();
    for (const secondaryContact of group) {
      log.push(`Merging contact ${secondaryContact.id} into ${primaryContact.id}`);
      callHubSpotApi(`<https://api.hubapi.com/crm/v3/objects/contacts/${primaryContact.id}/merge>`, 'post', { objectId: secondaryContact.id });
    }
  }
}

function getStoredContacts(sheet) {
  const data = sheet.getRange('B1:B').getValues();
  const contacts = data
    .filter(row =&gt; row[0] !== '')
    .map(row =&gt; JSON.parse(row[0]));

  return contacts;
}

function storeContacts(sheet, contacts) {
  const data = contacts.map(contact =&gt; [JSON.stringify(contact)]);
  sheet.getRange(1, 2, data.length, 1).setValues(data);
}```
