Hello everyone, I'm new here, I call on you because I have a problem with my custom code in the workflow, what I'm trying to do for several days is this path of actions: Recover the housing associated with the transaction, Recover the contacts associated with housing and then Associate the owners of housing to the current deal here is my code  :

const HubSpotClient = require('@HubSpot/api-client');
exports.main = async (event, callback) =&gt; {
console.log(process.env.HAPIKEY);

const HubSpot = new HubSpotClient.Client({
  accessToken: ''

});

  HubSpot.setApiKey(process.env.HAPIKEY);

console.log('inputFields:', event.inputFields);

  const dealID = event.inputFields['dealID'];

  // Vérifier si dealId est défini
  if (!dealID) {
    console.error("dealId is not defined");
    return;
  }
  const toObjectType = "DEAL";
  const toCustomType = "2-112304201";
  const limit = 20;


  try {
    console.log(HubSpotClient);
    console.log('dealID:', dealID);

    const apiCustomResponse = await HubSpot.crm.deals.associationsApi.getAll(dealID, toObjectType, toCustomType,limit);
    console.log(JSON.stringify(apiCustomResponse.body, null, 2));
    const customArray = apiCustomResponse.body.results ;
    const customId = customArray[0].id ;
    console.log("The custom object record is: " + customId );

    // Récupérer le logement associé à la transaction
    const logementId = customArray[0].properties.custom_logement_id.value;
    const logementApiResponse = await HubSpotClient.crm.objects.basicApi.getById(toCustomType, logementId);
    const logement = logementApiResponse.body;

    console.log("The logement record is: " + JSON.stringify(logement, null, 2));

   // Récupérer les contacts associés au logement
    const contactsApiResponse = await HubSpotClient.crm.contacts.searchApi.doSearch({ query:`custom_logement:${logementId}` });
    const contacts = contactsApiResponse.body.results;

  // Associer les propriétaires du logement au deal courant
  for (const contact of contacts) {
    const associationsApiResponse = await HubSpotClient.crm.contacts.associationsApi.getAll(contact.id, "lot_proprietaire", undefined, 50);
    const associations = associationsApiResponse.body.results;
  for (const association of associations) {
      await HubSpotClient.crm.contacts.associationsApi.create(contact.id, 'deal', dealID, 'lot_proprietaire', association.id);
    }
  }

    callback({
      outputFields: {
        dealID: dealID,
      }
    });

  } catch (e) {
    e.message === 'HTTP request failed'
    ? console.error(JSON.stringify(e.response, null, 2))
    : console.error(e)
  }
}



