async function logEmailEngagement(contactId, subject, body, ownerId) {
  try {
    const properties = {
      "hs_timestamp": new Date().toISOString(),
      "hs_email_direction": "EMAIL",
      "hs_email_text": emailContent,
      "hs_email_subject": subject
    };
    const SimplePublicObjectInputForCreate = {
      associations: [
        {
          "types": [
            {
              "associationCategory": "HubSpot_DEFINED",
              "associationTypeId": 210
            }
          ],
          "to": {
            "id": contactId
          }
        }
      ],
      properties
    };

    const apiResponse = await HubSpotClient.crm.objects.emails.basicApi.create(SimplePublicObjectInputForCreate);
    return apiResponse;
  } catch (e) {
    if (e.message === 'HTTP request failed') {
      console.error(JSON.stringify(e.response, null, 2));
    } else {
      console.error(e);
    }
    return null;
  }
}
