const axios = require('axios');
const FormData = require('form-data');
const fs = require('fs');


async function pushZip() {
    try {
        let data = new FormData();
        data.append('file', fs.createReadStream('../Downloads/file.zip'));

        let config = {
        method: 'put',
        maxBodyLength: Infinity,
        url: '<https://api.hubapi.com/cms/v3/source-code/published/content/myfile.zip>', // <http://myfile.zip/|myfile.zip> is the name the file will have in hs
        headers: {
            'Authorization': 'Bearer my_token',
            ...data.getHeaders()
        },
        data : data
        };

        const response = await axios.request(config);
        console.log(<http://response.data/|response.data>);
        return <http://response.data/|response.data>;
    } catch (error) {
        console.log(error);
    }
}

async function unZip() {
    try {

        const response = await axios({
            method: 'post',
            url: '<https://api.hubapi.com/cms/v3/source-code/extract/myfile.zip>',
            headers: {
                authorization: 'Bearer my_token'
            },
            data: {}
        })

        console.log(response);
        return response;
    } catch (error) {
        console.log(error);
    }

}

async function main () {

    //await pushZip();
    await unZip();
}

main();
