```import {
  Collapsible,
  CollapsibleContent,
  CollapsibleTrigger,
} from '../ui/collapsible.js';
import { Button } from '../ui/button.js';
import FormContainerFieldsType from '../modules/FormContainer/fields.type.js';
import { Form } from '@HubSpot/cms-components';

export default function CollapsibleForm({
  fieldValues,
}: {
  fieldValues: FormContainerFieldsType;
}) {
  return (
    &lt;Collapsible&gt;
      &lt;div className="flex justify-center flex-col text-center items-center space-y-4 max-w-4xl mx-auto"&gt;
        &lt;h3&gt;{fieldValues.heading}&lt;/h3&gt;

        &lt;p className="text-2xl"&gt;{fieldValues.text}&lt;/p&gt;

        &lt;p className="text-lg"&gt;&lt;/p&gt;
        &lt;CollapsibleTrigger asChild&gt;
          &lt;Button&gt;Solliciteer nu&lt;/Button&gt;
        &lt;/CollapsibleTrigger&gt;
      &lt;/div&gt;
      &lt;CollapsibleContent className="container"&gt;
        &lt;h2&gt;OPEN&lt;/h2&gt;
        &lt;Form fieldPath="form" /&gt;
      &lt;/CollapsibleContent&gt;
    &lt;/Collapsible&gt;
  );
}```

