&lt;div class="row-fluid-wrapper row-depth-1 row-number-1 "&gt;
  &lt;div class="row-fluid "&gt;
    &lt;div class="span12 spacing-resource" style="background:#011031;"&gt;
      &lt;div class="container"&gt;

      &lt;div class="sidebar-main-resou-cess"&gt;

        &lt;!-- set the filter by drop down, search bar, and submit button --&gt;
        &lt;form id="form_id" method="get"&gt;
          &lt;div class="listingss"&gt;
          &lt;h5&gt;Filter resources:&lt;/h5&gt;
        &lt;/div&gt;

            &lt;div class="topics-sidebar listingss"&gt;
            &lt;select id="topic_listing" class="topic-content-sidebar"&gt;
              &lt;option&gt;&lt;a href="javascript:void(0);" class="all"&gt;Topics&lt;/a&gt;&lt;/option&gt;
              {% for row in hubdb_table_rows('Table_id', queryparams) %}
              &lt;option value="{{ row["types"].name }}"&gt;&lt;a href="javascript:void(0);" class="digital_marketing"&gt;{{ row["types"].name }}&lt;/a&gt;&lt;/option&gt;
               {% endfor %}
            &lt;/select&gt;


          &lt;/div&gt;

          &lt;div class="content-type-sidebar listingss"&gt;
            &lt;select id="type_listing" class="topic-content-sidebar"&gt;
              &lt;option&gt;&lt;a href="javascript:void(0);" class="all"&gt;Content Types&lt;/a&gt;&lt;/option&gt;
              {% for row in hubdb_table_rows('Table_id', queryparams) %}
              &lt;option value="{{ row["topic"].name }}"&gt;&lt;a href="javascript:void(0);" class="digital_marketing"&gt;{{ row["topic"].name }}&lt;/a&gt;&lt;/option&gt;
               {% endfor %}
            &lt;/select&gt;

          &lt;/div&gt;
            &lt;!-- search form start --&gt;
          &lt;div class="sidebar-resources-inner listingss"&gt;

            &lt;div class="search-button-main"&gt;
  &lt;div id="search-input"&gt;&lt;/div&gt;

            &lt;/div&gt;
          &lt;/div&gt;
          &lt;!-- search form end --&gt;
        &lt;/form&gt;

        &lt;/div&gt;
      &lt;/div&gt;
    &lt;/div&gt;
    &lt;div class="container"&gt;
    &lt;div class="span12"&gt;
      {% set queryparams = "&amp;orderBy=-hs_created_at" %}
      {% set table = hubdb_table_rows(Table_id, queryparam) %}

      {% if table == [] %}
        &lt;p class='align-center'&gt;Sorry, no listings found for that Search. Try changing your fiter and search again.&lt;/p&gt;
      {% else %}
        &lt;div class="outer-post-resorces" id="resource-container"&gt;
          {% for row in hubdb_table_rows('Table_id', queryparams) %}

            &lt;div id="resource-container-listing" class="inner-post-resorces {{ row["topic"].name }} {{ row["types"].name }}"&gt;
              &lt;div class="inner-post-image-reso"&gt;&lt;img src="{{ row["IMAGE"].url}}"&gt;&lt;/div&gt;
              &lt;div class="inner-post-design"&gt;
              &lt;div class="inner-post-resorces-image"&gt;{{ <http://row.cat|row.cat> }}&lt;/div&gt;
              &lt;div class="inner-post-link-reso"&gt;&lt;h5&gt;&lt;a href=""&gt;{{ row.name }}&lt;/a&gt;&lt;/h5&gt;&lt;/div&gt;
              &lt;div class="display-none-hover-button-resorce simplest-search-anchor Button-cta"&gt;&lt;a href="{{ row.url }}" target="_blank"&gt;View Details&lt;/a&gt;&lt;/div&gt;
            &lt;/div&gt;
            &lt;/div&gt;
          {% endfor %}

        &lt;/div&gt;
      {% endif %}
    &lt;/div&gt;
    &lt;/div&gt;
  &lt;/div&gt;
&lt;/div&gt;

