{% for location in hubdb_table_rows(&lt;location table id&gt;) %}
      &lt;h3 class="section-title"&gt;&lt;span&gt;{{ location.location_name }}&lt;/span&gt;&lt;/h3&gt;
        {% set eventFilter = "&amp;orderBy=event_date" %}
        {% for foreign_row in hubdb_table_rows(&lt;event table id&gt;,eventFilter) %}
          {% if (foreign_row['event_date']|datetimeformat('%j')) &gt;= (local_dt|datetimeformat('%j')) %}
            {% for demo in foreign_row.event_location %}
              {% if demo.hs_id == location.hs_id %}
                &lt;div class="event tw-py-4"&gt;
                  &lt;div class="event-info"&gt;
                    &lt;b&gt;{{ foreign_row.event_date|datetimeformat('%d.%m.%Y') }}&lt;/b&gt; - {{ foreign_row.host_name }}
                    &lt;br&gt;
                    &lt;b&gt;{{ module.location_label }}&lt;/b&gt; - {{ foreign_row.event_name }}
                    {% if foreign_row.event_note %}
                    &lt;br&gt;
                    &lt;small class="tw-mt-4 tw-block"&gt;{{ foreign_row.event_note }}&lt;/small&gt;
                    {% endif %}
                  &lt;/div&gt;
                  &lt;div class="event-button"&gt;
                    {% if foreign_row.event_availability.id == 1 %}
                      &lt;a href="javascript:void(0)" data-target="#bookingModal" data-activate="modal" class="btn"&gt;{{ module.seats_available_btn }}&lt;/a&gt;
                    {% elif foreign_row.event_availability.id == 2 %}
                      &lt;a href="javascript:void(0)" data-target="#bookingModal" data-activate="modal" class="btn-orange-300"&gt;{{ module.almost_sold_out_btn }}&lt;/a&gt;
                    {% elif foreign_row.event_availability.id == 3 %}
                      &lt;a href="javascript:void(0)" class="btn-orange-100"&gt;{{ module.sold_out_btn }}&lt;/a&gt;
                    {% endif %}
                  &lt;/div&gt;
                &lt;/div&gt;
                &lt;hr&gt; 
              {% endif %}
            {% endfor %}
          {% endif %}
        {% endfor %}
      {% endfor %}
