```{% if dynamic_page_hubdb_row %}
            &lt;section class="event-detail"&gt;
                &lt;h1&gt;{{ dynamic_page_hubdb_row.event_name }}&lt;/h1&gt;
                &lt;div class="richtext-box tw-mt-4"&gt;
                  {{ dynamic_page_hubdb_row.event_description }}
                &lt;/div&gt;
                &lt;hr class="tw-my-6" /&gt;
                &lt;div class="event-essencial"&gt;
                  &lt;p&gt;{{ module.detail_page.event_location_label }} : 
                    &lt;b&gt;
                      {% for evLocation in dynamic_page_hubdb_row.event_location %}
                        {{ evLocation.location_name }}
                        {% if loop.last != true %} , {% endif %}
                      {% endfor %}
                    &lt;/b&gt;
                  &lt;/p&gt;
                  &lt;p class="tw-mb-4"&gt;{{ module.detail_page.event_date_label }} : &lt;b&gt;{{ dynamic_page_hubdb_row.event_date|datetimeformat('%d.%m.%Y') }}&lt;/b&gt;&lt;/p&gt;
                  {% if dynamic_page_hubdb_row.event_availability.id == 1 %}
                    &lt;a href="javascript:void(0)" data-target="#bookingModal" data-activate="modal" class="btn"&gt;{{ module.seats_available_btn }}&lt;/a&gt;
                  {% elif dynamic_page_hubdb_row.event_availability.id == 2 %}
                    &lt;a href="javascript:void(0)" data-target="#bookingModal" data-activate="modal" class="btn-orange-300"&gt;{{ module.almost_sold_out_btn }}&lt;/a&gt;
                  {% elif dynamic_page_hubdb_row.event_availability.id == 3 %}
                    &lt;a href="javascript:void(0)" class="btn-orange-100"&gt;{{ module.sold_out_btn }}&lt;/a&gt;
                  {% endif %}
                &lt;/div&gt;
              &lt;/section&gt;  
          {% elif dynamic_page_hubdb_table_id %}
              &lt;section class="section comedy-events" {% if module.module_name %}id="{{ module.module_name }}"{% endif %}&gt;
                &lt;div&gt;
                  {% if module.description %}
                    &lt;div class="richtext-box tw-mb-12"&gt;
                        {{ module.description }}
                    &lt;/div&gt;
                  {% endif %}
                  {% for location in hubdb_table_rows(parent_table_id) %}
                    &lt;h3 class="section-title"&gt;&lt;span&gt;{{ location.location_name }}&lt;/span&gt;&lt;/h3&gt;
                    {% set eventFilter = "&amp;orderBy=event_date" %}
                    {% for foreign_row in hubdb_table_rows(child_table_id,eventFilter) %}
                      {#{% if (foreign_row['event_date']|datetimeformat('%j')) &gt;= (local_dt|datetimeformat('%j')) %}#}
                        {% for demo in foreign_row.event_location %}
                          {% if demo.hs_id == location.hs_id %}
                            &lt;div class="event tw-py-4"&gt;
                              &lt;div class="event-info"&gt;
                                &lt;b&gt;{{ foreign_row.event_date|datetimeformat('%d.%m.%Y') }}&lt;/b&gt; - {{ foreign_row.host_name }}
                                &lt;br&gt;
                                &lt;b&gt;{{ module.location_label }}&lt;/b&gt; - {{ foreign_row.event_name }}
                                {% if foreign_row.event_note %}
                                &lt;br&gt;
                                &lt;small class="tw-mt-4 tw-block"&gt;{{ foreign_row.event_note }}&lt;/small&gt;
                                {% endif %}
                              &lt;/div&gt;
                              &lt;div class="event-button md:tw-text-right"&gt;
                                {% if foreign_row.event_availability.id == 1 %}
                                  &lt;a href="javascript:void(0)" data-target="#bookingModal" data-event="{{ foreign_row.event_date|datetimeformat('%d.%m.%Y') }} - {{ foreign_row.hs_name }}" data-activate="modal" class="btn"&gt;{{ module.seats_available_btn }}&lt;/a&gt;
                                {% elif foreign_row.event_availability.id == 2 %}
                                  &lt;a href="javascript:void(0)" data-target="#bookingModal" data-activate="modal" class="btn-orange-300"&gt;{{ module.almost_sold_out_btn }}&lt;/a&gt;
                                {% elif foreign_row.event_availability.id == 3 %}
                                  &lt;a href="javascript:void(0)" class="btn-orange-100"&gt;{{ module.sold_out_btn }}&lt;/a&gt;
                                {% endif %}
                                {% if foreign_row.event_description %}
                                  &lt;div class="tw-mt-1"&gt;
                                    &lt;a href="{{ request.path }}/{{ foreign_row.hs_path }}"&gt;{{ module.know_more_button_label }}&lt;/a&gt;
                                  &lt;/div&gt;
                                {% endif %}
                              &lt;/div&gt;
                            &lt;/div&gt;
                            &lt;hr&gt; 
                          {% endif %}
                        {% endfor %}
                      {#{% endif %}#}
                    {% endfor %}
                  {% endfor %}
              &lt;/div&gt;
              &lt;/section&gt;
          {% endif %}```
