```{# SECTION SETTINGS #}
{% set margin = module.section.margin %}
{% set marginBot = module.section.margin_bottom %}
{% set color = module.section.color %}
{% set background_color = module.section.background_color %}
{% set background_top = module.section.background_top %}
{% set background_angled = module.section.background_angled %}
{% set p_v_align = module.section.parent_v_align %}
{% set p_h_align = module.section.parent_h_align %}
{% set center = module.section.center %}


&lt;section class="section--{{ margin }} section--mb-{{ marginBot }} content--{{ color }} background--{{ background_color }} {% if background_top %}background_top{% endif %} {% if background_angled %}background_angled{% endif %}"&gt;
  &lt;div class="container"&gt;
    &lt;div class="row {{ p_v_align }}-sm {{ p_h_align }}-sm {% if center %}center-xs{% endif %}"&gt;

      {# COLUMNS #}      
      {% for column in module.columns %}

      &lt;div class="col-xs-12 col-md-{{ column.size }} {% if column.centered %}col--centered{% endif %} {% if column.image_position %}first-xs last-md{% endif %}"&gt;

        {% for item in column.contents %}

        {# PREHEADER #}
        {% if item.type == 'preheader' %}
        &lt;div class="preheader" data-aos="fade-up"&gt;
          &lt;img class="preheader__icon" src="{{ item.preheader.icon.src }}" alt="{{ item.preheader.icon.alt }}"&gt;
          &lt;p class="preheader__text"&gt;{{ item.preheader.text }}&lt;/p&gt;
        &lt;/div&gt;
        {% endif %}

        {# HEADER  #}
        {% if item.type == 'header' %}
        &lt;h{{ item.header.type }} data-aos="fade-up"&gt;{{ item.header.text }}&lt;/h{{ item.header.type }}&gt;
        {% endif %}

        {# TEXT #}
        {% if item.type == 'text' %}
        &lt;p data-aos="fade-up" data-aos-delay="100"&gt;{{ item.description }}&lt;/p&gt;
        {% endif %}

        {# BUTTON  #}
        {% if item.type == 'button' %}
          {% if item.button.link.url.href %}
            {% set url = item.button.link.url.href %}
          {% else %}
            {% set url = "#" %}
          {% endif %}
        &lt;a href="{{ url }}" class="btn btn--{{ item.button.color }} {{ item.button.unbounce_class_name }} {% if item.button.toggle %}btn--arrow icon--right{% endif %}" target="_self" data-aos="fade-up" data-aos-delay="200"&gt;
          {{ item.button.text}}
        &lt;/a&gt;
        {% endif %}

        {# POPUP BUTTON #}
        {% if item.type == 'pop_up_button' %}
          {#{% if item.pop_up_button.link.url.href %}
            {% set url = item.pop_up_button.link.url.href %}
          {% else %}
            {% set url = "#" %}
          {% endif %}#}
        &lt;a href="javascript:void(0);" class="btn btn--{{ item.pop_up_button.color }} {{ item.pop_up_button.unbounce_class_name }} {% if item.pop_up_button.toggle %}btn--arrow icon--right{% endif %}" target="_self" data-aos="fade-up" data-aos-delay="200" data-micromodal-trigger="popup-form" id="btn-records" {{ item.pop_up_button.text }}&lt;/a&gt;

{# POPUP BUTTON SCRIPT #}
&lt;div class="modal micromodal-slide" id="popup-form" aria-hidden="true"&gt;
  &lt;div class="modal__overlay" tabindex="-1" data-micromodal-close&gt;
    &lt;div class="modal__container" role="dialog" aria-modal="true" aria-labelledby="modal-1-title"&gt;
      &lt;div class="modal__header"&gt;
        &lt;button class="modal__close" aria-label="Close modal" data-micromodal-close&gt;&lt;/button&gt;
      &lt;/div&gt;
      &lt;div class="modal__content"&gt;
        &lt;h2 class="modal__title text--center"&gt;Get Your Personalized ELR Today!&lt;/h2&gt;
        &lt;p class="text--center"&gt;Get an experiential learning record with your school's personalized info to share with your team.&lt;/p&gt;
         &lt;!--[if lte IE 8]&gt;
      &lt;script charset="utf-8" type="text/javascript" src="//js.hsforms.net/forms/v2-legacy.js"&gt;&lt;/script&gt;
      &lt;![endif]--&gt;
      &lt;script charset="utf-8" type="text/javascript" src="//js.hsforms.net/forms/v2.js"&gt;&lt;/script&gt;
      &lt;script&gt;
        hbspt.forms.create({
          portalId: {{ item.pop_up_button.portalid }},
          formId: {{ item.pop_up_button.formid }},
          css: '',
          cssRequired: '',
          cssClass: '',
          submitButtonClass: 'btn--block',
        });
      &lt;/script&gt;
      &lt;/div&gt;
    &lt;/div&gt;
  &lt;/div&gt;
&lt;/div&gt;


{{ require_js("<https://unpkg.com/@popperjs/core@2>", "footer") }}
{{ require_js("<https://unpkg.com/tippy.js@6>", "footer") }}
{{ require_css("<https://unpkg.com/swiper/swiper-bundle.min.css>") }}
{{ require_js("<https://unpkg.com/swiper/swiper-bundle.min.js>", "head") }}

        {% endif %}

        {# AUTHOR #}
        {% if item.type == 'author' %}
        &lt;div class="content__author" data-aos="fade-up"&gt;
          &lt;img class="content__author-image" src="{{ item.author.image.src }}" alt="{{ item.author.image.alt }}"&gt;
          &lt;div class="content__author-text"&gt;
            &lt;p class="content__author-name"&gt;{{ item.author.a_name }}&lt;/p&gt;
            &lt;p class="content__author-title"&gt;{{ item.author.role }}&lt;/p&gt;
          &lt;/div&gt;
        &lt;/div&gt;
        {% endif %}

        {# IMAGE #}
        {% if item.type == 'image' %}
        &lt;img src="{{ item.image.src }}" alt="{{ item.image.alt }}" data-aos="fade-up"&gt;
        {% endif %}

        {# VIDEO #}
        {% if item.type == 'video' %}
        &lt;video class="content__video" id="{{ item.video.optional_id }}" autoplay="autoplay" loop="loop" muted="" playsinline="" defaultmuted=""&gt;&lt;source src="{{ item.video.file }}" type="video/mp4"&gt;&lt;/video&gt;
        &lt;img class="content__video--mobile" src="{{ item.video.image.src }}" alt="item.video.image.alt"&gt;
        {% endif %}

        {# RICH TEXT #}
        {% if item.type == "rich_text" %}
        &lt;div&gt;{{ item.description_right }}&lt;/div&gt;
        {% endif %}

        {# LIST #}
        {% if item.type == "list" %}
        &lt;ul&gt;
          {% for list_item in item.list.item %}
          &lt;li data-aos="fade-up"&gt;{{ list_item }}&lt;/li&gt;
          {% endfor %}
        &lt;/ul&gt;
        {% endif %}

        {# FEATURES #}
        {% if item.type == "features" %}
        &lt;div class="features-horizontal__feature-footer"&gt;
              &lt;div class="feature-footer-yellow"&gt;
                &lt;a href="{{ item.features.link_field.url }}"&gt;&lt;img class="feature-footer__icon" src="{{ item.features.icon.src }}" alt="{{ item.features.icon.alt }}" data-tippy-content="{{ item.features.tooltip }}" height="50" width="50"&gt;&lt;/a&gt;
          &lt;/div&gt;
            &lt;/div&gt;
        {% endif %}

        {# SCHOOL TESTIMONIALS #}
        {% if item.type == "testimonials" %}
        &lt;div class="content__testimonials" data-aos="fade-up"&gt;
          &lt;p class="content__testimonial-heading" data-aos="fade-up"&gt;{{ item.testimonials.text }}&lt;/p&gt;
          &lt;div class="content__logos"&gt;                  
            {% for img in item.testimonials.logo %}
            &lt;img src="{{ img.src }}" alt="{{ logo.alt }}" data-aos="fade-up" loading=lazy&gt;  
            {% endfor %}
          &lt;/div&gt;
        &lt;/div&gt; 
        {% endif %}        

        {% endfor %}
      &lt;/div&gt;
      {% endfor %}
    &lt;/div&gt;
  &lt;/div&gt;
&lt;/section&gt;

&lt;script src="<https://unpkg.com/@popperjs/core@2>"&gt;&lt;/script&gt;
&lt;script src="<https://unpkg.com/tippy.js@6>"&gt;&lt;/script&gt;```
