`&lt;table cellpadding="2" cellspacing="2" width="600" style="max-width: 100%;"&gt;`
&lt;tr align=“center”&gt;
{% set posts = blog_recent_posts(‘default’, 3) %}
{% for post in posts %}
        &lt;td width=“173" height=“150”&gt;
            &lt;a href=“{{ post.absolute_url }}”
               &gt;
             &lt;img style=“width: 173px; height: 150px; object-fit: cover;” src=“{{ post.featured_image }}” /&gt;
            &lt;/a&gt;
      &lt;/td&gt;
{% endfor %}
  &lt;/tr&gt;
&lt;/table&gt;
