            url: ajaxURL,
            dataType: "json",
            beforeSend: function(request) {
                request.setRequestHeader("Content-Type","application/x-www-form-urlencoded;carset=utf-8");
            },
            success: function(data) {
                var result = $.parseJSON(data);
                console.log(result);
                $("#spnAccessToken").html(result.access_token);
                $("#spnRefreshToken").html(result.refresh_token);
            },
            error: function(data) {
                var err = $.parseJSON(data);
                console.log(err);
                $("#dvError").removeClass("no-display");
                $("#dvError").html("&lt;strong&gt;Error:&lt;/strong&gt; " + err.error + "&lt;br/&gt;" + err.error_description);
            }
        });

