&lt;script src="https://code.jquery.com/ui/1.12.1/jquery-ui.min.js"&gt;&lt;/script&gt;
&lt;script&gt;
  $(window).load(function() {

    $(".hs_hotel_name__c input").autocomplete({
      source: function(request, response) {
        $.ajax({
          url: "<https://app.otainsight.com/ajax_select/ajax_lookup/publichotelinfochannel>",
          dataType: "json",
          data: {
            term: request.term
          },
          success: function(data) {
            // response(data);   
            response($.map(data, function(item) {
              return {
                label: <http://item.pk|item.pk>,
                value: item.match,
                city: item.city,
                country: item.country_name
              }
            }));
            // console.log(data);
            if (!$.trim(data)) {
              $(".hs_pk_value input").val("null");
              $(".hs_city input").val("null");
              $(".hs_country select").val("");
            }
          },
          error: function(data) {
            console.log("error");
          }
        });
      },
      minLength: 3,
      select: function(event, ui, item) {
        var name = ui.item.value;
        var pk = ui.item.label;
        var city = ui.item.city;
        var country = ui.item.country;
        $(".hs_hotel_name__c input").val(name);
        $(".hs_pk_value input").val(pk);
        $(".hs_city input").val(city).prop('type', 'text').change();
        $('.hs_city').css('display', 'block');
        $(".hs_country select").val(country);
        return false;
      }
    }).data("ui-autocomplete")._renderItem = function(ul, item) {
      return $("&lt;li&gt;").data("ui-autocomplete-item", item).append(item.value).appendTo(ul);
    };

  });
&lt;/script&gt;
