$.getJSON("<https://www.ariase.com/getHeaderFooter?callback=?>", function( data ) {
      var htmlHEADER = data.header;
      $('.header-container-wrapper .header-container').append(htmlHEADER);

      var htmlFOOTER = data.footer;
      $('.footer-container-wrapper .footer-container').append(htmlFOOTER);

      var linkCSS = data.css;
      $('.footer-container-wrapper .footer-container').append('&lt;link rel="stylesheet" media="screen" href="'+linkCSS+'"&gt;');

      var linkJS = data.js;
      $('.footer-container-wrapper .footer-container').append("&lt;script src='"+linkJS+"' type='text/javascript' defer='defer'&gt;&lt;/script&gt;");
    });
