{% set sections = range(1, 8) %}
{% choice "sections_field" label="How many content modules?", value="1", choices="{{ sections }}", export_to_template_context=True %}

{% set layout = '[
"Banner",
"Content: Big",
"Content: Standard",
"Content: With CTA Double",
"Content: Standard Pink",
"CTA: Single",
"CTA: Double",
"Image: Full Width"
]' %}
{% for section in sections|split(',', widget_data.sections_field.value) %}
		{% choice "layout_field" label="Layout for block (Content)", value="", choices="{{ layout }}", export_to_template_context=True, unique_in_loop=True %}
{% endfor %}

{% block modules %}
	{% for widget in widget_data %}
		{% if loop.index0 &lt;= widget_data.sections_field.value %}
				{% if widget.value == "Banner" %}
						{% module "module_12345" path="/Email/Modules - Content/Banner" label="Banner" no_wrapper='True' %}
				{% elif widget.value == "Content: Big" %}
						{% module "module_2" path="/Email/Modules - Content/Content only big" label="Content: Big" no_wrapper='True' %}
				{% elif widget.value == "Content: Standard" %}
						{% module "module_3" path="/Email/Modules - Content/Content only" label="Content: Standard" no_wrapper='True' %}
				{% elif widget.value == "Content: With CTA Double" %}
						{% module "module_15685930483822" path="/Email/Modules - Content/Content with CTA Double" label="Content: With CTA Double" no_wrapper='True' %}
        {% elif widget.value == "Content: Standard Pink" %}
						{% module "module_4" path="/Email/Modules - Content/Content - Pink" label="Content: Standard Pink" no_wrapper='True' %}
				{% elif widget.value == "CTA: Single" %}
						{% module "module_5" path="/Email/Modules - Content/CTA - Single" label="CTA: Single" no_wrapper='True' %}
				{% elif widget.value == "CTA: Double" %}
						{% module "module_6" path="/Email/Modules - Content/CTA - Double" label="CTA: Double" no_wrapper='True' %}
				{% elif widget.value == "Image: Full Width" %}
						{% module "module_7" path="/Email/Modules - Content/Image" label="Image: Full Width" no_wrapper='True' %}
				{% endif %}
		{% endif %}
  {% endfor %}
