```// Update preferences button with id="hs_show_banner_button" is hidden on the page with display: none

/**
 * Get the contents of a cookie
 * Thanks <https://www.w3schools.com/js/js_cookies.asp>
 * @param {string} cname
 * @returns {string}
 */
function getCookie(cname) {
    let name = cname + "=";
    let decodedCookie = decodeURIComponent(document.cookie);
    let ca = decodedCookie.split(";");

    for (let i = 0; i &lt; ca.length; i++) {
        let c = ca[i];
        while (c.charAt(0) == " ") {
            c = c.substring(1);
        }
        if (c.indexOf(name) == 0) {
            return c.substring(name.length, c.length);
        }
    }
    return "";
}

