MODULE.HTML:
&lt;div class="hub-content-form-section" style="background-color: {{ module.background_color.color }};"&gt;
  &lt;div class="maxContainer"&gt;
    &lt;div class="row-fluid hub-content-form" id="hub-content-form"&gt;
      &lt;div class="span8 hub-content"&gt;
        {{ module.content }}
        &lt;!-- Blog Social Share --&gt;
        &lt;div class="blog_social_share"&gt;
          &lt;div class="tagline"&gt;CONNECT WITH US&lt;/div&gt;
          {% if module.facebook_url %}
            &lt;a href="{{ module.facebook_url.href }}" class="facebook" title="Facebook" target="blank" rel=""&gt;&lt;/a&gt;
          {% endif %}
          {% if module.twitter_url %}
            &lt;a href="{{ module.twitter_url.href }}" class="twitter" title="Twitter" target="blank" rel=""&gt;&lt;/a&gt;
          {% endif %}
          {% if module.instagram_url %}
            &lt;a href="{{ module.instagram_url.href }}" class="instagram" title="Instagram" target="blank" rel=""&gt;&lt;/a&gt;
          {% endif %}
          {% if module.pinterest_url %}
            &lt;a href="{{ module.pinterest_url.href }}" class="pinterest" title="Pinterest" target="blank" rel=""&gt;&lt;/a&gt;
          {% endif %}
        &lt;/div&gt;
        &lt;div class="hub-bottom-content"&gt;
          {{ module.bottom_content }}
        &lt;/div&gt;
      &lt;/div&gt;
      &lt;div class="span4 hub-form-wrapper"&gt;
        &lt;div class="hub-form"&gt;
          &lt;div class="form-heading"&gt;
            {{ module.form_heading }}
          &lt;/div&gt;
          {% form
            form_to_use="{{ module.form.form_id }}"
            response_response_type="{{ module.form.response_type }}"
            response_message="{{ module.form.message }}"
            response_redirect_id="{{ module.form.redirect_id }}"
            response_redirect_url="{{module.form.redirect_url}}"
            gotowebinar_webinar_key="{{ module.form.gotowebinar_webinar_key }}"
          %}
        &lt;/div&gt;
      &lt;/div&gt;
    &lt;/div&gt;
  &lt;/div&gt;
&lt;/div&gt;

MODULE.CSS:
.hub-content-form-section{
  padding: 60px 20px;
  color: #545454;
}
.hub-content h1{
    margin-top: 15px;
    margin-bottom: 15px;
    font-size: 54px;
    line-height: 1.2em;
    font-weight: 300;
    color: #545454;
}
.hub-content h2{
  font-size: 32px;
  line-height: 36px;
  margin-top: 20px;
  font-weight: bold;
  margin-bottom: 10px;

}
.hub-content h3{
  font-size: 24px;
  line-height: 1.4em;
  margin-top: 0;
  margin-bottom: 10px;
  font-weight: 400;

}
.hub-content h4{
  color: #fff;
}
.hub-content p{
  font-size: 16px;
  line-height: 1.4em;
  margin-top: 0;
  margin-bottom: 10px;
}
.hub-content a{

}
.hub-content a:hover{
  color: #097B30;
  text-decoration: underline;
}
.blog_social_share{
  text-align: left;
  padding-bottom: 40px;
  padding-top: 30px;
  margin-top: 40px;
  margin-bottom: 40px;
  padding: 17px 10px 18px;
  border-top: 2px solid #E5E5E5;
  border-bottom: 2px solid #E5E5E5;
}
.blog_social_share a img,
.blog_social_share a#icon_4,
.blog_social_share a#icon_6{
  display: none;
}
.blog_social_share a{
  width: 40px!important;
  height: 40px;
  margin-right: 10px;
  border-radius: 50px;
  background-color: transparent;
  background-position: 50% 50%;
  -webkit-transition: all 200ms ease;
  transition: all 200ms ease;
  background-repeat: no-repeat;
  display: inline-block;
  position: relative;
  border: 2px solid #6F2580!important;
  text-align: center;
  text-decoration: none;
}
.blog_social_share a:hover{
  -webkit-transform: scale(1.05);
  -ms-transform: scale(1.05);
  transform: scale(1.05);
  text-decoration: none;
}

.blog_social_share a#icon_1:before,
.blog_social_share .facebook:before{
  content: '\f09a';
  font-family: 'FontAwesome';
  font-size: 20px;
}
.blog_social_share a#icon_2:before{
  content: '\f0e1';
  font-family: 'FontAwesome';
  font-size: 20px;
}
.blog_social_share a#icon_3:before,
.blog_social_share .twitter:before{
  content: '\f099';
  font-family: 'FontAwesome';
  font-size: 20px;
}
.blog_social_share a#icon_5:before{
  content: '\f0e0';
  font-family: 'FontAwesome';
  font-size: 20px;
}
.blog_social_share .instagram:before{
  content: '\f16d';
  font-family: 'FontAwesome';
  font-size: 20px;
}
.blog_social_share .pinterest:before{
  content:'\f0d2';
  font-family: 'FontAwesome';
  font-size: 20px;
}
.blog_social_share .tagline{
  opacity: 0.9;
  font-size: 14px;
  letter-spacing: 2px;
  font-weight: 600;
  text-transform: uppercase;
  padding-bottom: 5px;
}
#hub-content-form .hub-content{
  width: 67%!important;
  padding-right: 40px;
}
#hub-content-form .hub-form-wrapper{
  width: 33%!important;
  margin-left: 0!important;
}

.hub-form{
  padding: 28px 20px 20px;
  background-color: #6F2580;
  color: #333;
}
.hub-form .form-heading h3{
  font-size: 24px;
  line-height: 30px;
  margin-top: 0;
  font-weight: bold;
  margin-bottom: 10px;
  color: #333;
}
.hub-form h3.form-title{
  display: none;
}
.hub-form form &gt; div.hs-form-field label{
  display: none;
}
.hub-form form &gt; div.hs-form-field input,
.hub-form form &gt; div.hs-form-field textarea{
  height: 45px;
  padding: 8px 12px;
  font-size: 14px;
  line-height: 1.42857143;
  color: #333333;
  vertical-align: middle;
  background-color: #FFFFFF;
  border: 1px solid #CCCCCC;
  width: 100%;
}
.hub-form form &gt; div.hs-form-field{
  margin-bottom: 10px;
}
.hub-form form input::-webkit-input-placeholder { /* Chrome/Opera/Safari */
  color: #A99B99;
}
.hub-form form input::-moz-placeholder { /* Firefox 19+ */
  color: #A99B99;
}
.hub-form form input:-ms-input-placeholder { /* IE 10+ */
  color: #A99B99;
}
.hub-form form input:-moz-placeholder { /* Firefox 18- */
  color: #A99B99;
}
.hub-form form &gt; div.hs-form-field ul.hs-error-msgs li label{
  font-size: 13px;
  line-height: 15px;
  padding: 0;
  display: inline-block;
  color: #fff;
}
.hub-form form div.hs-richtext,
.hub-form form div.hs-richtext p{
  color: rgba(44, 44, 44, 0.6);
  font-size: 14px;
  line-height: 1.4em;
  margin-bottom: 0;
}
.hub-form form div.hs-richtext p a{
  color: #702082;
}
.hub-form form div.hs-richtext p a:hover{
  text-decoration: underline;

}
.hub-form form div.hs-richtext{
  margin-bottom: 10px;
}
.hub-form form div.legal-consent-container .hs-fieldtype-booleancheckbox ul li label span{
  color: rgba(44, 44, 44, 0.6);
  font-size: 14px;
  line-height: 1.4em;
  position: relative;
  top: -1px;
}
.hub-form form div.hs_submit{
  margin-top: 17px;
}
.hub-form form div.hs_submit input{
  padding: 15px 20px;
  border-radius: 4px;
  background-color: #84BD00;
  font-size: 16px;
  line-height: 1.4em;
  -webkit-appearance: none;
  display: inline-block;
  color: white;
  border: 0;
  cursor: pointer;
  text-shadow: none;
  box-shadow: none;
  font-weight: 400;
}
.hub-form form div.hs_submit input:hover{
  text-shadow: none;
  box-shadow: none;
  border-color: none;
  background-color: #84BD00;
}
.submitted-message{
  text-align: center;
  padding: 25px 15px 25px;
  font-weight: bold;
  background: #fff;
  margin-bottom: 15px;
  color: #333;
  margin-top: 8px;
  font-size: 20px;
  line-height: 1.3em;
  border-top: 2px solid #333;
}
.banner-content p{
  font-weight: 400;
}
.maxContainer{
  max-width: 1140px;
    margin-right: auto;
    margin-left: auto;
}
.contact-info-list {
    margin-bottom: 30px;
    padding-left: 10px;
    border-left: 5px solid #E5E5E5;
    font-size: 20px;
    line-height: 1.4em;
    text-align: left;
}
<@U0777JK3WQ3> screen and (max-width: 767px){
  .hub-content h1{
    font-size:42px;
  }
  .hub-content-form-section{
    padding: 30px 20px 40px;
  }
  #hub-content-form .hub-content {
    width: 100%!important;
    padding-right: 0;
  }
  #hub-content-form .hub-form-wrapper {
    width: 100%!important;
    margin-left: 0!important;
  }
  .hub-form-wrapper{
    padding-top: 30px;
  }
  .hub-content h2{
    font-size: 30px;
    margin-top: 10px;
  }
  .hub-content h3{
    font-size: 22px;
  }


}

MODULE.JS:
$(function(){

  // blog social share
  // add dynamic id's
  var blog_ctr = 0;
  $('.blog_social_share .hs_cos_wrapper_type_social_sharing &gt; a').each(function(){
    blog_ctr++;
    $(this).attr('id', 'icon_'+blog_ctr);
    if ( blog_ctr &gt;= 5 ){
      blog_ctr=0;
    }
  });

  //remove spaces (nbsp)
  $(".hs_cos_wrapper_type_social_sharing").each(function () {
    var $this = $(this);
    $this.html($this.html().replace(/&amp;nbsp;/g, ''));
  });

});
const capitalize = (s) =&gt; {
  if (typeof s !== 'string') return ''
  return s.charAt(0).toUpperCase() + s.slice(1)
}

$(window).load(function(){

  $("input[name='firstname'],input[name='lastname']").focusout(function(){
   $(this).val(capitalize($(this).val().toLowerCase())).change(); //console.log(capitalize($(this).val().toLowerCase()));
  });
});
