```const axios = require('axios')

exports.main = async (context, sendResponse) =&gt; {
  console.log('2#$@#$@#$@#$@#$@#$@ #$@#$@#$ @#$ @#$ @#$ STARTING UPDATE ORDER JS')
  try {
    const { orderId, properties } = context.parameters

    let postData = {
      properties: properties
    }
    let config = {
      method: 'patch',
      url: `<https://api.hubapi.com/crm/v3/objects/2-31260895/${orderId}>`,
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${process.env.PRIVATE_APP_ACCESS_TOKEN}`
      },
      data: postData
    };
    console.log('updating order', orderId)
    console.log('properties:', properties)

    const { data } = await axios.request(config)
    console.log('response from update order:', data)

    return {
      statusCode: 200
    }

  } catch (err) {
    const error = err.response ? err.response.data : err.message
    console.log('error in update order serverless')
    console.error(error)
    return {
      statusCode: 500,
      message: JSON.stringify(error)
    }
  }
}```
