&lt;div class="blog-recent-post"&gt;
        &lt;h2&gt;Recent Posts&lt;/h2&gt;
        &lt;div class="blog-recent-list"&gt;
          {% set rec_posts = blog_recent_posts('group.id', 2) %}
          {% for rec_post in rec_posts %}
          &lt;div class="blog-recent"&gt;
            {% if rec_post.post_list_summary_featured_image %}
            &lt;div class="blog-recent__image"&gt;
              &lt;img src="{{ rec_post.featured_image }}" alt="{{ rec_post.name }}"&gt;
            &lt;/div&gt;
            {% endif %}
            &lt;time&gt;{{ rec_post.publish_date_local_time|datetimeformat('%B, %Y') }}&lt;/time&gt;
            &lt;h3&gt;{{ rec_post.name }}&lt;/h3&gt;
            &lt;a href="{{ rec_post.absolute_url }}" class="blog-related__cta"&gt;Keep Reading
            &lt;/a&gt;
          &lt;/div&gt;
          {% endfor %}
        &lt;/div&gt;
      &lt;/div&gt;
