body {
  font-family: verdana,sans-serif;
  margin: 0;
  padding: 0;
}


/* custom styles */
.skip-to-content {
  position: absolute;
  clip: rect(1px, 1px, 1px, 1px);
  width: 1px;
  height: 1px;
  top: -600px;
  left: 10px;
  background-color: #000;
  color: #fff;
  padding: 8px 16px;
  text-decoration: none;
  z-index: 100;
  overflow: hidden;
}

.skip-to-content:focus {
  top: 10px;
  position: static;
  clip: auto;
  height: auto;
  width: auto;
  overflow: visible;
}

.two-col-row {
    min-height: 620px;
}

header {
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 10px 6%;
    background: {{theme.header_background_color.color }};
    margin-bottom: 0;
    color: {{theme.header_text_color.color }};
    width: auto;
    min-height: 100px;
}

.wrapper {
display: flex;
  flex-direction: row;
}

.col {
width: 100%;
}

.wrapper.img-left {
flex-direction: row-reverse;
}


/* team module css */
.team-member-repeater {
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
  gap: 15px;
}
@media (max-width: 900px) {
  .team-member-repeater {
    grid-template-columns: 1fr 1fr;
  }
}

