                &lt;!-- COL 1 --&gt;
                &lt;div class="col-md-10 mx-auto col-lg-6"&gt;
                    &lt;div&gt;
                        {% module "top_text" path="@HubSpot/rich_text" label="Top Form Text" %}
                    &lt;/div&gt;

                    &lt;div class="p-4 p-md-5 border rounded-3 bg-light"&gt;
                        {% module "form_top" path="@HubSpot/form" label="Form Top" %}
                    &lt;/div&gt;
