const HubSpot = require('@HubSpot/api-client');
const partner_object = "[...]";

exports.main = async (event) =&gt; {
    try {
        const HubSpotClient = new HubSpot.Client({ accessToken: process.env.~patoken~ });

        // Extracting objectId from event.object
        const companyId = event.object.objectId;

        const activitiesResponse = await HubSpotClient.crm.associations.v4.basicApi.getPage("company", companyId, ["engagement"]);
        const activities = activitiesResponse.body.results || [];

        const associationsResponse = await HubSpotClient.crm.associations.v4.basicApi.getPage(
            "company", companyId, ["contacts", "deals", "attachments"]
        );
        const associations = associationsResponse.body.results || [];

        const contactIds = associations.filter(a =&gt; a.type === "contact").map(a =&gt; a.id);
        const dealIds = associations.filter(a =&gt; a.type === "deal").map(a =&gt; a.id);
        const attachmentIds = associations.filter(a =&gt; a.type === "attachment").map(a =&gt; a.id);

        const batchRequests = [
            HubSpotClient.crm.associations.batchApi.create(
                partner_object,
                event.object.objectId,
                "engagement",
                activities.map(activity =&gt; activity.id)
            ),
            HubSpotClient.crm.associations.batchApi.createBatch(
                partner_object,
                event.object.objectId,
                "contact",
                contactIds
            ),
            HubSpotClient.crm.associations.batchApi.createBatch(
                partner_object,
                event.object.objectId,
                "deal",
                dealIds
            ),
            HubSpotClient.crm.associations.batchApi.createBatch(
                partner_object,
                event.object.objectId,
                "attachment",
                attachmentIds
            )
        ];

        await Promise.all(batchRequests);

        console.log("Associations created successfully.");

        return { statusCode: 200 };
    } catch (error) {
        console.error("Error processing event:", error);
        return { statusCode: 500 };
    }
};
